sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // Validate input
        if (!inputs.pluginID || typeof inputs.pluginID !== 'string' || inputs.pluginID.trim() === '') {
            outputs.pluginresult = false;
            outputs.error_message = 'Plugin ID is missing or invalid.';
            return;
        }

        // Check if the GlidePluginManager API is available
        if (typeof GlidePluginManager === 'undefined' || typeof GlidePluginManager.isActive !== 'function') {
            outputs.pluginresult = false;
            outputs.error_message = 'GlidePluginManager is unavailable.';
            return;
        }

        // Main logic
        var isPluginActive = GlidePluginManager.isActive(inputs.pluginID);
        outputs.pluginresult = isPluginActive;
        outputs.error_message = '';

    } catch (e) {
        // Handle unexpected exceptions
        outputs.pluginresult = false;
        outputs.error_message = 'An unexpected error occurred: ' + e.message;
    }
})(inputs, outputs);

