sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // 1Ô∏è‚É£ Input (type: Date)
        var inputDate = new GlideDateTime(inputs.date);

        // üóìÔ∏è WEEK OF MONTH CALCULATION (Monday‚ÄìSunday)
        var firstDay = new GlideDateTime(inputDate);
        firstDay.setDayOfMonthUTC(1);

        var inputDay = parseInt(inputDate.getDayOfMonthUTC());   // e.g., 28
        var firstDayOfWeek = firstDay.getDayOfWeekUTC();         // 1=Monday, 7=Sunday

        // Calculate week number within month
        var weekOfMonth = Math.ceil((inputDay + (firstDayOfWeek - 1)) / 7);

        // ISO week of year
        var weekOfYear = inputDate.getWeekOfYearUTC();
        

        // üß† HUMAN-READABLE DAY & MONTH
        var dayMap = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
        var monthMap = [
            "January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ];

        var dayName = dayMap[inputDate.getDayOfWeekUTC() - 1];
        var monthName = monthMap[inputDate.getMonthUTC() - 1];

        // Leap year check
        var isoYear = inputDate.getYearUTC();
        var isLeapYear = (isoYear % 4 === 0 && isoYear % 100 !== 0) || (isoYear % 400 === 0);

        // ‚úÖ OUTPUTS
        outputs.day_name = dayName;
        outputs.month_name = monthName;
        outputs.week_of_month = weekOfMonth;
        outputs.week_of_year = weekOfYear;
        outputs.iso_year = isoYear;
        outputs.is_leap_year = isLeapYear;
        outputs.action_status = "success";
        outputs.error_message = "";

    } catch (e) {
        // ‚ùå Error handling
        outputs.action_status = "error";
        outputs.error_message = e.toString();
    }

})(inputs, outputs);

